function Ensure-GitRepo {
    param([string]$RepoPath)
    if (-not (Ensure-GitAvailable)) { return $false }
    if (-not (Test-Path (Join-Path $RepoPath '.git'))) {
        Write-ErrMsg "La ruta '$RepoPath' no contiene un repositorio git."
