import zipfile, os, io, json, textwrap, re, hashlib, sys, pathlib, itertools

zip_path = "/mnt/data/SEED02_Config_Seguridad.zip"
result = {}

if not os.path.exists(zip_path):
    print("ZIP no encontrado:", zip_path)
else:
    with zipfile.ZipFile(zip_path, 'r') as z:
        file_list = z.namelist()
        # Gather basic info
        result['file_count'] = len(file_list)
        result['files'] = file_list
        # Read text files (md, ps1, txt, csv, json, yml)
        texts = {}
        for name in file_list:
            lower = name.lower()
            if lower.endswith(('.md', '.ps1', '.txt', '.csv', '.json', '.yml', '.yaml')):
                try:
                    data = z.read(name)
                    # try utf-8 decode with fallback
                    for enc in ['utf-8', 'utf-8-sig', 'latin-1']:
                        try:
                            text = data.decode(enc)
                            break
                        except Exception:
                            text = None
                    if text is None:
                        continue
                    texts[name] = text
                except Exception as e:
                    texts[name] = f"<<ERROR LECTURA: {e}>>"
        result['text_file_count'] = len(texts)
        # Extract structure and headings
        outline = {}
        function_like = {}
        code_blocks = {}
        for name, txt in texts.items():
            # headings in markdown
            heads = re.findall(r'^(#{1,6})\s+(.+)$', txt, flags=re.M)
            # list items with numeric prefixes for subparts e.g., 02.0, 02.1
            subparts = re.findall(r'^\s*(?:[-*]|\d+[.)])\s*(\d{2}\.\d)\s+([^\n]+)$', txt, flags=re.M)
            # Also catch bare 02.x at line start
            subparts2 = re.findall(r'^\s*(\d{2}\.\d)\s+([^\n]+)$', txt, flags=re.M)
            combined_subparts = list(dict.fromkeys(subparts + subparts2))
            outline[name] = {
                "headings": heads,
                "subparts": combined_subparts
            }
            # function signatures or cmdlets
            funcs = set()
            # PowerShell function definitions
            for m in re.finditer(r'(?im)^\s*function\s+([A-Za-z0-9_-]+)\s*\{', txt):
                funcs.add(m.group(1))
            # Cmdlets mentioned in inline code or plain
            # simple heuristic: Identify words with hyphen typical of cmdlets
            for m in re.finditer(r'`?([A-Za-z]+-[A-Za-z][A-Za-z0-9]+)`?', txt):
                funcs.add(m.group(1))
            # .NET types used frequently
            for m in re.finditer(r'\[([A-Za-z0-9_.]+)\]', txt):
                if '.' in m.group(1):
                    funcs.add(m.group(1))
            function_like[name] = sorted(funcs)
            # Capture code blocks for a quick sample
            blocks = re.findall(r'```(?:powershell|ps1|ps|bash|sh|cmd|text|json|yaml|yml)?\s*\n(.*?)\n```', txt, flags=re.S|re.I)
            if not blocks:
                # also triple-tilde
                blocks = re.findall(r'~~~\s*\n(.*?)\n~~~', txt, flags=re.S)
            code_blocks[name] = [b.strip()[:4000] for b in blocks[:3]]  # sample
        
        result['outline'] = outline
        result['functions'] = function_like
        # Try to infer core subparts across files: collect all 02.x labels
        subparts_all = []
        for name in texts:
            sp = outline[name]["subparts"]
            for lab, title in sp:
                subparts_all.append((lab, title))
        # Unique ordered by lab
        seen = {}
        for lab, title in subparts_all:
            seen.setdefault(lab, title)
        result['subparts_unique'] = sorted(seen.items(), key=lambda x: x[0])
        
        # Summarize likely key docs: prefer filenames including 'SEED02' or 'Config' or 'Seguridad'
        key_docs = [n for n in texts if re.search(r'(?i)seed02|config|seguridad', n)]
        result['key_docs'] = key_docs[:10]

    print(json.dumps({
        "file_count": result['file_count'],
        "text_file_count": result['text_file_count'],
        "key_docs": result['key_docs'],
        "subparts_unique": result['subparts_unique'][:20],
    }, ensure_ascii=False, indent=2)[:4000])

