def read_guess(path):
    data = path.read_bytes()
    # try utf-8
    for enc in ['utf-8-sig','utf-16le','utf-16','gb18030','cp936','cp1252','latin-1']:
        try:
            return enc, data.decode(enc)
        except Exception:
            pass
    return 'bytes', data

for p in paths:
    enc, s = read_guess(p)
    print("====", p.name, "enc:", enc, "len:", len(s))
    print(s)

