function Show-AnimatedWpf {
  param(
    [string]$Msg,
    [int]$Seconds,
    [string]$Pos,
    [string]$ThemeSel,
    [bool]$Keep,
    [bool]$FullOpt
  )

  try {
    Write-Progress -Activity "Aviso" -Status "Inicializando WPF" -PercentComplete 10
    Add-Type -AssemblyName PresentationCore, PresentationFramework, WindowsBase
  }
  catch { return $false }

  $result = $false
  $threadScript = {
    param($Msg,$Seconds,$Pos,$ThemeSel,$Keep,$FullOpt)
    try {
      Add-Type -AssemblyName PresentationCore, PresentationFramework, WindowsBase | Out-Null

      # Colores de tema
      if ($ThemeSel -eq 'Light') {
        $bgWindow = '#FFF5F5F5' # fondo ventana lleno
        $bgCard   = '#FFFFFFFF'
        $fg       = '#222222'
        $shadowC  = '#33000000'
      } else {
        $bgWindow = '#FF0F0F10'
        $bgCard   = '#F0222222'
        $fg       = '#FFFFFF'
        $shadowC  = '#55000000'
      }

      # Ventana
      $window = New-Object System.Windows.Window
      $window.WindowStyle = 'None'
      $window.ResizeMode = 'NoResize'
      $window.Topmost = $true
      $window.ShowInTaskbar = $false

      if ($FullOpt) {
        $window.AllowsTransparency = $false
        $window.Background = [Windows.Media.BrushConverter]::new().ConvertFromString($bgWindow)
        $window.SizeToContent = 'Manual'
        $window.Left = [SystemParameters]::WorkArea.Left
        $window.Top  = [SystemParameters]::WorkArea.Top
        $window.Width  = [SystemParameters]::WorkArea.Width
        $window.Height = [SystemParameters]::WorkArea.Height
      }
      else {
        $window.AllowsTransparency = $true
        $window.Background = [Windows.Media.BrushConverter]::new().ConvertFromString('#00FFFFFF')
        $window.SizeToContent = 'WidthAndHeight'
        $window.WindowStartupLocation = 'CenterScreen'
      }

      # Root grid
      $root = New-Object System.Windows.Controls.Grid
      $root.RenderTransformOrigin = '0.5,0.5'
      $scale = New-Object System.Windows.Media.ScaleTransform 1,1
      $root.RenderTransform = $scale

      # Tarjeta
      $border = New-Object System.Windows.Controls.Border
      $border.CornerRadius = '24'
      $border.Padding = '28'
      $border.Background = [Windows.Media.BrushConverter]::new().ConvertFromString($bgCard)
      $border.Effect = New-Object System.Windows.Media.Effects.DropShadowEffect -Property @{ BlurRadius=18; ShadowDepth=0; Opacity=0.45; Color=([Windows.Media.ColorConverter]::ConvertFromString($shadowC)) }
      $border.HorizontalAlignment = 'Center'
      $border.VerticalAlignment   = 'Center'

      if ($FullOpt) {
        $border.Width  = [SystemParameters]::WorkArea.Width  * 0.9
        $border.Height = [SystemParameters]::WorkArea.Height * 0.9
      }

      # Texto
      $tb = New-Object System.Windows.Controls.TextBlock
      $tb.Text = $Msg
      $tb.TextWrapping = 'Wrap'
      $tb.TextAlignment = 'Center'
      $tb.Foreground = [Windows.Media.BrushConverter]::new().ConvertFromString($fg)
      $tb.FontFamily = 'Segoe UI Emoji'
      $tb.FontSize = if ($FullOpt) { 36 } else { 22 }

      $border.Child = $tb
      $root.Children.Add($border) | Out-Null
      $window.Content = $root
      $window.Opacity = 0

      # Posicionamiento si no es Full
      if (-not $FullOpt) {
        $margin = 28
        $window.Loaded.Add({
          $window.Measure([System.Windows.Size]::new([SystemParameters]::WorkArea.Width,[SystemParameters]::WorkArea.Height))
          $window.Arrange([System.Windows.Rect]::new(0,0,$window.DesiredSize.Width,$window.DesiredSize.Height))
          $w = $window.ActualWidth; $h = $window.ActualHeight
          switch ($Pos) {
            'TopLeft'     { $window.Left = [SystemParameters]::WorkArea.Left + $margin;  $window.Top = [SystemParameters]::WorkArea.Top + $margin }
            'TopRight'    { $window.Left = [SystemParameters]::WorkArea.Right - $w - $margin; $window.Top = [SystemParameters]::WorkArea.Top + $margin }
            'BottomRight' { $window.Left = [SystemParameters]::WorkArea.Right - $w - $margin; $window.Top = [SystemParameters]::WorkArea.Bottom - $h - $margin }
            'BottomLeft'  { $window.Left = [SystemParameters]::WorkArea.Left + $margin;  $window.Top = [SystemParameters]::WorkArea.Bottom - $h - $margin }
            default       { $window.WindowStartupLocation = 'CenterScreen' }
          }
        })
      }

      # Cierre con clic o tecla cuando Keep
      if ($Keep) {
        $window.Add_MouseDown({ $window.Close(); [System.Windows.Threading.Dispatcher]::CurrentDispatcher.InvokeShutdown() })
        $window.Add_KeyDown({ $window.Close(); [System.Windows.Threading.Dispatcher]::CurrentDispatcher.InvokeShutdown() })
      }

      # Animaciones
      $window.Loaded.Add({
        $easeIn  = New-Object System.Windows.Media.Animation.QuadraticEase
        $easeOut = New-Object System.Windows.Media.Animation.QuadraticEase
        $easeOut.EasingMode = [System.Windows.Media.Animation.EasingMode]::EaseOut

        $fadeIn = New-Object System.Windows.Media.Animation.DoubleAnimation(0,1,[TimeSpan]::FromMilliseconds(240))
        $fadeIn.EasingFunction = $easeIn
        $window.BeginAnimation([System.Windows.Window]::OpacityProperty, $fadeIn)

        $scaleAnim = New-Object System.Windows.Media.Animation.DoubleAnimation(0.95,1,[TimeSpan]::FromMilliseconds(220))
        $scaleAnim.EasingFunction = $easeOut
        $scale.BeginAnimation([System.Windows.Media.ScaleTransform]::ScaleXProperty, $scaleAnim)
        $scale.BeginAnimation([System.Windows.Media.ScaleTransform]::ScaleYProperty, $scaleAnim)

        if (-not $Keep) {
          $timer = New-Object System.Windows.Threading.DispatcherTimer
          $timer.Interval = [TimeSpan]::FromSeconds($Seconds)
          $timer.Add_Tick({
            $timer.Stop()
            $fadeOut = New-Object System.Windows.Media.Animation.DoubleAnimation(1,0,[TimeSpan]::FromMilliseconds(220))
            $fadeOut.Completed.Add({ $window.Close(); [System.Windows.Threading.Dispatcher]::CurrentDispatcher.InvokeShutdown() })
            $window.BeginAnimation([System.Windows.Window]::OpacityProperty, $fadeOut)
          })
          $timer.Start()
        }
      })

      $null = $window.Show()
      $script:__WpfLaunched = $true
      [System.Windows.Threading.Dispatcher]::Run()
      $script:__WpfOk = $true
    }
    catch {
      $script:__WpfOk = $false
    }
  }

  try {
    Write-Progress -Activity "Aviso" -Status "Lanzando ventana WPF" -PercentComplete 40
    $t = New-Object System.Threading.Thread($threadScript)
    $t.SetApartmentState([System.Threading.ApartmentState]::STA)
    $t.IsBackground = $true
    $t.Start(@($Msg,$Seconds,$Pos,$ThemeSel,$Keep,$FullOpt))
    # Espera breve para considerar "lanzado"
