  }
  finally {
    Write-Progress -Activity "Aviso" -Completed
  }
  return $result
}

function Show-AnimatedMshta {
  param(
    [string]$Msg,
    [int]$Seconds,
    [string]$Pos,
    [string]$ThemeSel,
    [bool]$Keep,
    [bool]$FullOpt
  )
  try {
    Write-Progress -Activity "Aviso" -Status "Construyendo HTML" -PercentComplete 60

    $safe   = ConvertTo-HtmlLiteral -Value $Msg
    $keepJs = if ($Keep) { "true" } else { "false" }
    $fullJs = if ($FullOpt) { "true" } else { "false" }

    if ($ThemeSel -eq 'Light') {
      $bgWindow = 'rgba(245,245,245,1)'
      $bgCard   = 'rgba(255,255,255,0.98)'
      $fg       = '#222222'
      $shadow   = '0 16px 48px rgba(0,0,0,0.28)'
    } else {
      $bgWindow = 'rgba(15,15,16,1)'
      $bgCard   = 'rgba(34,34,34,0.95)'
      $fg       = '#FFFFFF'
      $shadow   = '0 16px 48px rgba(0,0,0,0.45)'
    }

    $dimCss = if ($FullOpt) { "min-width:80vw;max-width:90vw;min-height:80vh;" } else { "min-width:300px;max-width:60vw;" }
    $cssPos = if ($FullOpt) {
      'left:50%; top:50%; transform:translate(-50%,-50%);'
    } else {
      switch ($Pos) {
        'TopLeft'     { 'left:28px; top:28px;' }
        'TopRight'    { 'right:28px; top:28px;' }
        'BottomRight' { 'right:28px; bottom:28px;' }
        'BottomLeft'  { 'left:28px; bottom:28px;' }
        default       { 'left:50%; top:50%; transform:translate(-50%,-50%) scale(0.95);' }
      }
    }

    $html = @"
<html>
<head>
  <meta http-equiv="X-UA-Compatible" content="IE=edge" />
  <meta charset="utf-8" />
  <title>Aviso</title>
  <hta:application applicationname="Aviso" border="none" caption="no" sysmenu="no" scroll="no" singleinstance="yes" />
  <style>
    html,body{margin:0;height:100%;overflow:hidden;background:$bgWindow;font-family:""Segoe UI Emoji"",""Segoe UI"",Arial,sans-serif}
    .wrap{position:fixed;$cssPos z-index:2147483647;}
    .card{
      background:$bgCard;color:$fg;box-shadow:$shadow;border-radius:24px;padding:28px;$dimCss
      opacity:0; animation:pop 0.24s ease-out forwards;
    }
    .text{font-size:36px; text-align:center; line-height:1.35; word-wrap:break-word}
    @keyframes pop { from{opacity:0; transform:scale(0.96)} to{opacity:1; transform:scale(1)} }
    @keyframes fadeout { to{opacity:0} }
  </style>
</head>
<body>
  <div class="wrap"><div class="card" id="card"><div class="text">$safe</div></div></div>
  <script type="text/javascript">
    (function(){
      var keep = $keepJs;
      var full = $fullJs;
      try{ window.moveTo(0,0); window.resizeTo(screen.availWidth, screen.availHeight);}catch(e){}
      if(!keep){
        var ms = $([int]([Math]::Round($Seconds * 1000)));
        setTimeout(function(){
          var c = document.getElementById('card');
          c.style.animation = 'fadeout 0.22s ease-in forwards';
          setTimeout(function(){ window.close(); }, 240);
        }, ms);
      } else {
        document.addEventListener('click', function(){ window.close(); });
        document.addEventListener('keydown', function(){ window.close(); });
      }
    })();
  </script>
</body>
</html>
"@

    $tmp = Join-Path -Path ([IO.Path]::GetTempPath()) -ChildPath ("Aviso_{0}.hta" -f ([Guid]::NewGuid().ToString('N')))
    [IO.File]::WriteAllText($tmp, $html, [Text.UTF8Encoding]::new($false))

    Write-Progress -Activity "Aviso" -Status "Lanzando mshta" -PercentComplete 90
    $psi = New-Object System.Diagnostics.ProcessStartInfo
    $psi.FileName = "$env:WINDIR\System32\mshta.exe"
    $psi.Arguments = "`"$tmp`""
    $psi.UseShellExecute = $true
    [System.Diagnostics.Process]::Start($psi) | Out-Null
    Start-Sleep -Milliseconds 200
    Write-Progress -Activity "Aviso" -Completed
    return $true
  }
  catch {
    Write-Progress -Activity "Aviso" -Completed
    Write-Warning "mshta fall贸: $($_.Exception.Message)"
    return $false
  }
}

