def show(path):
    print(f"\n--- {path} ---")
    with z.open(path) as f:
        data = f.read()
    for enc in ('utf-8','utf-16','latin-1'):
        try:
            txt = data.decode(enc)
            break
        except:
            continue
    else:
        txt = None
    if txt is None:
        print("[binary]")
        return
    for i, line in enumerate(txt.splitlines()[:200], 1):
        print(f"{i:02d}: {line}")
    print(f"... ({len(txt.splitlines())} lines)")
    
for p in paths:
    show(p)

