# Build a tree representation
from collections import defaultdict

z = zipfile.ZipFile("/mnt/data/mnt.zip", 'r')
paths = [i.filename for i in z.infolist()]
# Function to build nested dict
tree = {}
for p in paths:
    parts = [x for x in p.strip("/").split("/") if x]
    d = tree
    for i, part in enumerate(parts):
        if part.endswith("."): # unlikely
            part = part[:-1]
        if i == len(parts)-1:
            d.setdefault(part, {} if p.endswith("/") else None)
        else:
            d = d.setdefault(part, {})
            
def render(d, prefix=""):
    lines = []
    items = sorted(d.items(), key=lambda kv: (kv[1] is None, kv[0].lower()))
    for i,(name,child) in enumerate(items):
        connector = "└── " if i == len(items)-1 else "├── "
        if child is None:
            lines.append(prefix + connector + name)
        else:
            lines.append(prefix + connector + name + "/")
            ext = "    " if i == len(items)-1 else "│   "
            lines.extend(render(child, prefix + ext))
    return lines

print("\n".join(render(tree)))

